import React, { useState } from 'react';
import { disableStation, increaseVolume, sayHello } from '../../services/yandexIntegrationService';

const YandexIntegration = () => {
    const [stationData, setStationData] = useState('');
    const [volumeData, setVolumeData] = useState('');
    const [helloData, setHelloData] = useState('');
    const [responseMessage, setResponseMessage] = useState('');

    const handleDisableStation = async () => {
        const response = await disableStation(stationData);
        setResponseMessage(response);
    };

    const handleIncreaseVolume = async () => {
        const response = await increaseVolume(volumeData);
        setResponseMessage(response);
    };

    const handleSayHello = async () => {
        const response = await sayHello(helloData);
        setResponseMessage(response);
    };

    return (
        <div>
            <h2>Yandex Integration</h2>
            <div>
                <h3>Disable Station</h3>
                <input
                    type="text"
                    value={stationData}
                    onChange={(e) => setStationData(e.target.value)}
                    placeholder="Station Data"
                />
                <button onClick={handleDisableStation}>Disable Station</button>
            </div>
            <div>
                <h3>Increase Volume</h3>
                <input
                    type="text"
                    value={volumeData}
                    onChange={(e) => setVolumeData(e.target.value)}
                    placeholder="Volume Data"
                />
                <button onClick={handleIncreaseVolume}>Increase Volume</button>
            </div>
            <div>
                <h3>Say Hello</h3>
                <input
                    type="text"
                    value={helloData}
                    onChange={(e) => setHelloData(e.target.value)}
                    placeholder="Hello Data"
                />
                <button onClick={handleSayHello}>Say Hello</button>
            </div>
            {responseMessage && <div><strong>Response:</strong> {responseMessage}</div>}
        </div>
    );
};

export default YandexIntegration;
