import React, { useState } from 'react';
import DeviceList from './components/Devices/DeviceList';
import DeviceForm from './components/Devices/DeviceForm';
import RoomList from './components/Rooms/RoomList';
import RoomForm from './components/Rooms/RoomForm';
import ScheduleList from './components/Schedules/ScheduleList';
import ScheduleForm from './components/Schedules/ScheduleForm';
import UserSettingList from './components/UserSettings/UserSettingList';
import UserSettingForm from './components/UserSettings/UserSettingForm';
import YandexIntegration from "./components/Yandex/YandexIntegration";

const App = () => {
    const [isEditingDevice, setIsEditingDevice] = useState(false);
    const [currentDevice, setCurrentDevice] = useState(null);
    const [isEditingRoom, setIsEditingRoom] = useState(false);
    const [currentRoom, setCurrentRoom] = useState(null);
    const [isEditingSchedule, setIsEditingSchedule] = useState(false);
    const [currentSchedule, setCurrentSchedule] = useState(null);
    const [isEditingUserSetting, setIsEditingUserSetting] = useState(false);
    const [currentUserSetting, setCurrentUserSetting] = useState(null);

    const handleEditDevice = (device) => {
        setCurrentDevice(device);
        setIsEditingDevice(true);
    };

    const handleEditRoom = (room) => {
        setCurrentRoom(room);
        setIsEditingRoom(true);
    };

    const handleEditSchedule = (schedule) => {
        setCurrentSchedule(schedule);
        setIsEditingSchedule(true);
    };

    const handleEditUserSetting = (userSetting) => {
        setCurrentUserSetting(userSetting);
        setIsEditingUserSetting(true);
    };

    const handleDeviceSuccess = () => {
        setIsEditingDevice(false);
        setCurrentDevice(null);
    };

    const handleRoomSuccess = () => {
        setIsEditingRoom(false);
        setCurrentRoom(null);
    };

    const handleScheduleSuccess = () => {
        setIsEditingSchedule(false);
        setCurrentSchedule(null);
    };

    const handleUserSettingSuccess = () => {
        setIsEditingUserSetting(false);
        setCurrentUserSetting(null);
    };

    return (
        <div>
            <h1>Smart Home Management</h1>
            <h2>Devices</h2>
            {isEditingDevice ? (
                <DeviceForm device={currentDevice} onSuccess={handleDeviceSuccess} />
            ) : (
                <>
                    <DeviceList onEdit={handleEditDevice} />
                    <DeviceForm onSuccess={handleDeviceSuccess} />
                </>
            )}
            <h2>Rooms</h2>
            {isEditingRoom ? (
                <RoomForm room={currentRoom} onSuccess={handleRoomSuccess} />
            ) : (
                <>
                    <RoomList onEdit={handleEditRoom} />
                    <RoomForm onSuccess={handleRoomSuccess} />
                </>
            )}
            <h2>Schedules</h2>
            {isEditingSchedule ? (
                <ScheduleForm schedule={currentSchedule} onSuccess={handleScheduleSuccess} />
            ) : (
                <>
                    <ScheduleList onEdit={handleEditSchedule} />
                    <ScheduleForm onSuccess={handleScheduleSuccess} />
                </>
            )}
            <h2>User Settings</h2>
            {isEditingUserSetting ? (
                <UserSettingForm userSetting={currentUserSetting} onSuccess={handleUserSettingSuccess} />
    ) : (
        <>
            <UserSettingList onEdit={handleEditUserSetting} />
            <UserSettingForm onSuccess={handleUserSettingSuccess} />
        </>
    )}
            <h2>Yandex Integration</h2>
            <YandexIntegration/>
</div>
);
};

export default App;
