import React, { useState } from 'react';
import TopBar from './components/TopBar';
import RoomDetails from '../src/components/Rooms/RoomDetails';
import YandexIntegration from '../src/components/Yandex/YandexIntegration';

const App = () => {
    const [selectedRoom, setSelectedRoom] = useState(null);
    const [devices, setDevices] = useState([
        { id: 1, name: 'Smart Light', status: 'On', roomId: "string" },
        { id: 2, name: 'Smart Thermostat', status: 'Off', roomId: "string" },
        { id: 3, name: 'Smart Speaker', status: 'On', roomId: "string" },
    ]);

    const handleEditDevice = (device) => {

    };

    const handleDeleteDevice = (deviceId) => {
        setDevices(devices.filter(device => device.id !== deviceId));
    };

    return (
        <div className="bg-purple-100 min-h-screen p-4">
            <h1 className="text-3xl font-bold text-center text-purple-800 mb-6">Smart Home Management</h1>
            <TopBar onSelectRoom={setSelectedRoom} />
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-4">
                {selectedRoom && (
                    <RoomDetails
                        room={selectedRoom}
                        devices={devices.filter(device => device.roomId === selectedRoom.id)}
                        onEditDevice={handleEditDevice}
                        onDeleteDevice={handleDeleteDevice}
                    />
                )}
                <YandexIntegration />
            </div>
        </div>
    );
};

export default App;
